package philosophe.thread2.actif;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;import java.util.concurrent.ThreadFactory;import philosophe.api.IGestBaguettes;public class LanceurPhiloActif {		public static void main(String[] args) {		int nbPhil = 5;		int nbServ = 3;		if (args.length > 0) {			nbPhil = Integer.parseInt(args[0], 10);			nbServ = Integer.parseInt(args[1], 10);		}  		ExecutorService serveurs = Executors.newFixedThreadPool(nbServ, new ThreadFactory() {			public Thread newThread(Runnable arg0) {				Thread t = new Thread(arg0);				t.setDaemon(true);				return t;			}			});				IGestBaguettes arbitre=new GestBagActif(nbPhil);		PhiloActif[] phil;		phil = new PhiloActif[nbPhil];				for (int i = 0; i < nbPhil; i++)			phil[i] = new PhiloActif(i, serveurs, arbitre, i, (i+1) % nbPhil);		for (int i = 0; i < nbPhil; i++)			phil[i].start();	}}