package philosophe.async.thread;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;import java.util.concurrent.ThreadFactory;import philosophe.api.IGestBaguettes1;public class LanceurPhiloAsync {		public static void main(String[] args) {		int nbPhil = 10;		int nbServ = 3;		if (args.length > 0) {			nbPhil = Integer.parseInt(args[0], 10);			nbServ = Integer.parseInt(args[1], 10);		}  		ExecutorService serveurs = Executors.newFixedThreadPool(nbServ, new ThreadFactory() {			public Thread newThread(Runnable arg0) {				Thread t = new Thread(arg0);				t.setDaemon(true);				return t;			}			});				IGestBaguettes1 arbitre=new GestBagAsync(nbPhil);		PhiloAsync[] phil;		phil = new PhiloAsync[nbPhil];				for (int i = 0; i < nbPhil; i++)			phil[i] = new PhiloAsync(i, serveurs, arbitre, i, (i+1) % nbPhil);		for (int i = 0; i < nbPhil; i++)			phil[i].start();	}}